import styled from "styled-components";

export const InputContainer = styled.div`
  position: relative;

  input {
    padding: 14px 34px 14px 14px;
    background: rgba(133, 143, 164, 0.15);
    border-radius: 5px;
    border: none;
    outline: none;

    &::placeholder {
      font-weight: 400;
      font-size: 14px;
      line-height: 1.4;
      color: #858FA4;
    }
  }
`;
