export const baseTheme = {
    colors: {
        primary: '#F53B49',
        secondary: '#1A1A25',
        lightSecondary: '#858FA4',
        success: '#2FC509',
        danger: '#F99808',
        light: '#FFF',
        bg: '#E5E4E8',
        font: '#19191B',
    },

    media: {
        extraLarge: '(max-width: 1140px)',
        large: '(max-width: 960px)',
        medium: '(max-width: 720px)',
        small: '(max-width: 540px)',
    },

    // in px
    sizes: {
        header: { height: 56 },
        container: { width: 1200 },
        footer: { height: 128 },
        modal: { width: 540 },
    },

    durations: {
        ms300: .3,
    },

    // z-index
    order: {
        header: 50,
        modal: 100,
    },
}
