import styled from "styled-components";
import {ButtonContainer, IconButton} from "../IconButton/IconButton";

export const FooterContainer = styled.div`
  padding: 88px 0 51px;
  background-color: #1A1A25;
`;

export const FooterBottom = styled.div`
  position: relative;
  padding-top: 51px;
  display: flex;


  &::before {
    content: '';
    position: absolute;
    top: -1px;
    left: 0;
    display: block;
    width: 100%;
    height: 1px;
    background: rgba(133, 143, 164, 0.15);
    border-radius: 1px;
  }
`;

export const FooterTop = styled.div``;

export const SocialButtons = styled.div`
  display: flex;
  gap: 10px;

  ${ButtonContainer} {
    path {
      fill: #909CB5;
      transition: all .3s ease;
    }

    &:hover {
      path {
        fill: #FFF;
      }
    }
  }
`;

export const PaymentMethods = styled.div`
  display: flex;
  align-items: center;
  gap: 30px;
`;

export const CopyRight = styled.p`
  font-weight: 400;
  font-size: 14px;
  line-height: 1.4;
  color: #858FA4;
`;

export const FooterLogo = styled.div`
  ${CopyRight} {

  }
`;
